mapper.insert:
/** * com.swygbro.packup.template.mapper.TemplateMapper > TBL_USER_TEMPLATE * 작성자 : 홍섭 * 설명 : 사용자 템플릿 저장 * */
SELECT IFNULL(MAX(TEMPLATE_NO), 0) + 1 FROM TBL_USER_TEMPLATE
INSERT INTO TBL_USER_TEMPLATE ( TEMPLATE_NO, TEMPLATE_NM, USER_NO, USER_ID, REG_DT, REG_ID, UPD_DT, UPD_ID, ALARM_DT, REPEAT_TYPE, ALARM_REPEAT_DAY, ALARM_TIME, CATE_NO ) VALUES ( #{templateNo}, #{templateNm}, #{userNo}, #{userId}, NOW(), #{userId}, NOW(), #{userId}, #{alarmDt}, #{repeatType}, #{alarmRepeatDay}, #{alarmTime}, #{cateNo} )

/** * com.swygbro.packup.template.mapper.TemplateMapper > TBL_USER_TEMPLATE_STEP * 작성자 : 홍섭 * 설명 : 사용자 템플릿 스텝 정보 저장 * */
SELECT IFNULL(MAX(TEMPLATE_STEP_NO), 0) + 1 FROM TBL_USER_TEMPLATE_STEP
INSERT INTO TBL_USER_TEMPLATE_STEP ( TEMPLATE_STEP_NO, TEMPLATE_NO, STEP, REG_DT, REG_ID, UPD_DT, UPD_ID, STEP_X, STEP_Y, DEL_YN ) VALUES ( #{templateStepNo}, #{templateNo}, #{step}, NOW(), #{regId}, NOW(), #{updId}, #{stepX}, #{stepY}, #{delYn} )

/** * com.swygbro.packup.template.mapper.TemplateMapper > TBL_USER_TEMPLATE_STEP_OBJ * 작성자 : 홍섭 * 설명 : 사용자 템플릿 스텝 오브젝트 저장 * */
SELECT IFNULL(MAX(TEMP_USER_OBJ_NO), 0) + 1 FROM TBL_USER_TEMPLATE_STEP_OBJ
INSERT INTO TBL_USER_TEMPLATE_STEP_OBJ ( TEMP_USER_OBJ_NO, TEMPLATE_STEP_NO, OBJ_NM, REG_DT, REG_ID, UPD_DT, UPD_ID, DEL_YN, OBJ_X, OBJ_Y, STEP, TEMPLATE_NO ) VALUES ( #{tempUserObjNo}, #{templateStepNo}, #{objNm}, NOW(), #{regId}, NOW(), #{updId}, #{delYn}, #{objX}, #{objY}, #{step}, #{templateNo} )

/** * com.swygbro.packup.template.mapper.TemplateMapper > TBL_USER_TEMPLATE_STEP_TEXT * 작성자 : 홍섭 * 설명 : 사용자 템플릿 스텝 텍스트 저장 * */
SELECT IFNULL(MAX(TEXT_NO), 0) + 1 FROM TBL_USER_TEMPLATE_STEP_TEXT
INSERT INTO TBL_USER_TEMPLATE_STEP_TEXT ( TEXT_NO, TEMPLATE_STEP_NO, TEXT, STEP_TEXT_X, STEP_TEXT_Y, TEMPLATE_NO, REG_DT, REG_ID, UPD_DT, UPD_ID, STEP ) VALUES ( #{textNo}, #{templateStepNo}, #{text}, #{stepTextX}, #{stepTextY}, #{templateNo}, NOW(), #{regId}, NOW(), #{updId}, #{step} )

mapper.select:
/** * com.swygbro.packup.template.mapper.TemplateMapper > getTemplatesByUserId * 작성자 : 홍섭 * 설명 : 템플릿 아이디 검색 * */ SELECT A.TEMPLATE_NO, A.USER_NO, A.USER_ID, A.REG_DT, A.REG_ID, A.UPD_DT, A.UPD_ID, A.TEMPLATE_NM, A.IS_FAVORITE, A.CATE_NO, CASE WHEN T.CATE_NM = 'office' then '업무' WHEN T.CATE_NM = 'daily' then '생활' WHEN T.CATE_NM = 'trip' then '여행' ELSE '' END AS CATE_NM FROM TBL_USER_TEMPLATE A , TBL_CATE T WHERE 1=1 AND A.DEL_YN = 'N' AND T.CATE_NO = A.CATE_NO AND A.USER_ID = #{userId}
AND IS_FAVORITE = 'Y'
AND A.CATE_NO = #{cateNo}
ORDER BY A.UPD_DT DESC
ORDER BY A.REG_DT DESC
ORDER BY A.ALARM_DT DESC
ORDER BY A.TEMPLATE_NM DESC
LIMIT #{pageSize} OFFSET #{offset}

mapper.update:
UPDATE TBL_USER_TEMPLATE_STEP
	SET 
		STEP_X = #{stepX},
		STEP_Y = #{stepY},
		DEL_YN = #{delYn},
		UPD_ID = #{userId},
		UPD_DT = NOW()
WHERE TEMPLATE_NO = #{templateNo}
AND TEMPLATE_STEP_NO = #{templateStepNo}
AND STEP = #{step}

UPDATE TBL_USER_TEMPLATE_STEP_OBJ
SET
	CATE_NO = #{cateNo},
	OBJ_NO = #{objNo},
	OBJ_NM = #{objNm},
	LOC_NUM = #{locNum},
	DEL_YN = #{delYn},
	UPD_ID = #{userId},
	UPD_DT = NOW(),
	STEP = #{step}
WHERE TEMP_USER_OBJ_NO = #{tempUserObjNo}
  AND TEMPLATE_STEP_NO = #{templateStepNo}
  AND STEP = #{step}
